#!/bin/bash
samtools merge -f /mnt/beegfs/gt156213e/murinae-seq/01-Assembly-data/06-Map/Mastomys-natalensis-mouse/hiseq4000-paired-3.merge.bam /mnt/beegfs/gt156213e/murinae-seq/01-Assembly-data/06-Map/Mastomys-natalensis-mouse/hiseq4000-paired-3/T4162_CKDL200159916-1a-DY0088-AK1680_HC3LGCCX2_L6_.fastp.decon.bam /mnt/beegfs/gt156213e/murinae-seq/01-Assembly-data/06-Map/Mastomys-natalensis-mouse/hiseq4000-paired-3/T4162_CKDL200159916-1a-DY0088-AK1680_HC3LGCCX2_L6_.fastp.decon.mkdup.bam &> /mnt/beegfs/gt156213e/murinae-seq/01-Assembly-scripts/logs/06.5-Post-map-logs/Mastomys-natalensis-hiseq4000-paired-3-merge-mouse.log
picard -Xmx10g MarkDuplicates I=/mnt/beegfs/gt156213e/murinae-seq/01-Assembly-data/06-Map/Mastomys-natalensis-mouse/hiseq4000-paired-3.merge.bam TMP_DIR=/mnt/beegfs/gt156213e/murinae-seq/01-Assembly-data/06-Map/Mastomys-natalensis-mouse/tmp O=/mnt/beegfs/gt156213e/murinae-seq/01-Assembly-data/06-Map/Mastomys-natalensis-mouse/hiseq4000-paired-3.merge.mkdup.bam M=/mnt/beegfs/gt156213e/murinae-seq/01-Assembly-data/06-Map/Mastomys-natalensis-mouse/Mastomys-natalensis-hiseq4000-paired-3-mkdup-metrics.txt CREATE_INDEX=true &> /mnt/beegfs/gt156213e/murinae-seq/01-Assembly-scripts/logs/06.5-Post-map-logs/Mastomys-natalensis-hiseq4000-paired-3-mkdup-mouse.log
samtools merge -f /mnt/beegfs/gt156213e/murinae-seq/01-Assembly-data/06-Map/Mastomys-natalensis-mouse/Mastomys-natalensis.bam /mnt/beegfs/gt156213e/murinae-seq/01-Assembly-data/06-Map/Mastomys-natalensis-mouse/hiseq4000-paired-3.merge.mkdup.bam &> /mnt/beegfs/gt156213e/murinae-seq/01-Assembly-scripts/logs/06.5-Post-map-logs/Mastomys-natalensis-merge-mouse.log
samtools index /mnt/beegfs/gt156213e/murinae-seq/01-Assembly-data/06-Map/Mastomys-natalensis-mouse/Mastomys-natalensis.bam
samtools depth -b /mnt/beegfs/gt156213e/murinae-seq/Targets/targets-mm10-coords.bed /mnt/beegfs/gt156213e/murinae-seq/01-Assembly-data/06-Map/Mastomys-natalensis-mouse/Mastomys-natalensis.bam > /mnt/beegfs/gt156213e/murinae-seq/01-Assembly-data/06-Map/Mastomys-natalensis-mouse/Mastomys-natalensis-depth.tab
samtools bedcov /mnt/beegfs/gt156213e/murinae-seq/Targets/targets-mm10-coords.bed /mnt/beegfs/gt156213e/murinae-seq/01-Assembly-data/06-Map/Mastomys-natalensis-mouse/Mastomys-natalensis.bam > /mnt/beegfs/gt156213e/murinae-seq/01-Assembly-data/06-Map/Mastomys-natalensis-mouse/Mastomys-natalensis-bedcov.tab
samtools stats -d -r /mnt/beegfs/gt156213e/ref-genomes/mm10/mm10.fa /mnt/beegfs/gt156213e/murinae-seq/01-Assembly-data/06-Map/Mastomys-natalensis-mouse/Mastomys-natalensis.bam > /mnt/beegfs/gt156213e/murinae-seq/01-Assembly-data/06-Map/Mastomys-natalensis-mouse/Mastomys-natalensis-stats.txt
samtools flagstat /mnt/beegfs/gt156213e/murinae-seq/01-Assembly-data/06-Map/Mastomys-natalensis-mouse/Mastomys-natalensis.bam > /mnt/beegfs/gt156213e/murinae-seq/01-Assembly-data/06-Map/Mastomys-natalensis-mouse/Mastomys-natalensis-flagstats.txt
mosdepth -x -b /mnt/beegfs/gt156213e/murinae-seq/Targets/targets-mm10-coords.bed /mnt/beegfs/gt156213e/murinae-seq/01-Assembly-data/06-Map/Mastomys-natalensis-mouse/Mastomys-natalensis /mnt/beegfs/gt156213e/murinae-seq/01-Assembly-data/06-Map/Mastomys-natalensis-mouse/Mastomys-natalensis.bam &> /mnt/beegfs/gt156213e/murinae-seq/01-Assembly-scripts/logs/06.5-Post-map-logs/Mastomys-natalensis-mosdepth-mouse.log
samtools view -bh /mnt/beegfs/gt156213e/murinae-seq/01-Assembly-data/06-Map/Mastomys-natalensis-mouse/Mastomys-natalensis.bam -L /mnt/beegfs/gt156213e/murinae-seq/Targets/targets-mm10-coords.bed > /mnt/beegfs/gt156213e/murinae-seq/01-Assembly-data/06-Map/Mastomys-natalensis-mouse/Mastomys-natalensis-targets.bam
samtools flagstat /mnt/beegfs/gt156213e/murinae-seq/01-Assembly-data/06-Map/Mastomys-natalensis-mouse/Mastomys-natalensis-targets.bam > /mnt/beegfs/gt156213e/murinae-seq/01-Assembly-data/06-Map/Mastomys-natalensis-mouse/Mastomys-natalensis-targets-flagstats.txt
