#!/bin/bash
gatk HaplotypeCaller -R /mnt/beegfs/gt156213e/murinae-seq/01-Assembly-data/08-Referee/Pseudomys-johnsoni-WAMM56280/Pseudomys-johnsoni-WAMM56280-referee-corrected.fa -I /mnt/beegfs/gt156213e/murinae-seq/01-Assembly-data/09-Remap/Pseudomys-johnsoni-WAMM56280/Pseudomys-johnsoni-WAMM56280.bam -stand-call-conf 30 --native-pair-hmm-threads 4 -O /mnt/beegfs/gt156213e/murinae-seq/01-Assembly-data/10-Varcall/Pseudomys-johnsoni-WAMM56280/Pseudomys-johnsoni-WAMM56280.vcf &> /mnt/beegfs/gt156213e/murinae-seq/01-Assembly-scripts/logs/10-Varcall-logs/Pseudomys-johnsoni-WAMM56280-haplotypecaller.log
gatk SelectVariants -V /mnt/beegfs/gt156213e/murinae-seq/01-Assembly-data/10-Varcall/Pseudomys-johnsoni-WAMM56280/Pseudomys-johnsoni-WAMM56280.vcf -O /mnt/beegfs/gt156213e/murinae-seq/01-Assembly-data/10-Varcall/Pseudomys-johnsoni-WAMM56280/Pseudomys-johnsoni-WAMM56280.snps.vcf -select-type SNP -xl-select-type MIXED -xl-select-type SYMBOLIC &> /mnt/beegfs/gt156213e/murinae-seq/01-Assembly-scripts/logs/10-Varcall-logs/Pseudomys-johnsoni-WAMM56280-selectsnps.log
bcftools filter -m+ -e "MQ < 30.0 || DP < 5 || DP > 60" -e 'ALT="*"' -s Filter --threads 4 -Oz -o /mnt/beegfs/gt156213e/murinae-seq/01-Assembly-data/10-Varcall/Pseudomys-johnsoni-WAMM56280/Pseudomys-johnsoni-WAMM56280.snps.filter.vcf /mnt/beegfs/gt156213e/murinae-seq/01-Assembly-data/10-Varcall/Pseudomys-johnsoni-WAMM56280/Pseudomys-johnsoni-WAMM56280.snps.vcf &> /mnt/beegfs/gt156213e/murinae-seq/01-Assembly-scripts/logs/10-Varcall-logs/Pseudomys-johnsoni-WAMM56280-filter.log
tabix /mnt/beegfs/gt156213e/murinae-seq/01-Assembly-data/10-Varcall/Pseudomys-johnsoni-WAMM56280/Pseudomys-johnsoni-WAMM56280.snps.filter.vcf
bcftools consensus -f /mnt/beegfs/gt156213e/murinae-seq/01-Assembly-data/08-Referee/Pseudomys-johnsoni-WAMM56280/Pseudomys-johnsoni-WAMM56280-referee-corrected.fa -o /mnt/beegfs/gt156213e/murinae-seq/01-Assembly-data/10-Varcall/Pseudomys-johnsoni-WAMM56280/Pseudomys-johnsoni-WAMM56280iupac-consensus.fa -I -e "FILTER='Filter'" /mnt/beegfs/gt156213e/murinae-seq/01-Assembly-data/10-Varcall/Pseudomys-johnsoni-WAMM56280/Pseudomys-johnsoni-WAMM56280.snps.filter.vcf &> /mnt/beegfs/gt156213e/murinae-seq/01-Assembly-scripts/logs/10-Varcall-logs/Pseudomys-johnsoni-WAMM56280-consensus.log
