#!/bin/bash
samtools merge -r /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Paraleptomys-sp/Paraleptomys-sp.sorted.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Paraleptomys-sp/hiseq4000-paired-1/SM065_CKDL190143344-1a-D706-AK1682_H7275BBXX_L3_.fastp.decon.bam
java -jar ~/bin/picard.jar MarkDuplicates I=/scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Paraleptomys-sp/Paraleptomys-sp.sorted.bam O=/scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Paraleptomys-sp/Paraleptomys-sp.sorted.mkdup.bam M=/scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Paraleptomys-sp/Paraleptomys-sp-mkdup-metrics.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/targets-rnor6-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Paraleptomys-sp/Paraleptomys-sp.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Paraleptomys-sp/Paraleptomys-sp-target-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Paraleptomys-sp/Paraleptomys-sp-target-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Paraleptomys-sp/Paraleptomys-sp-avg-target-depth.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/tiles-rnor6-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Paraleptomys-sp/Paraleptomys-sp.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Paraleptomys-sp/Paraleptomys-sp-tile-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Paraleptomys-sp/Paraleptomys-sp-tile-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Paraleptomys-sp/Paraleptomys-sp-avg-tile-depth.txt
samtools flagstat /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Paraleptomys-sp/Paraleptomys-sp.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Paraleptomys-sp/Paraleptomys-sp-flagstats.txt
