#!/bin/bash
gatk HaplotypeCaller -R /mnt/beegfs/gt156213e/murinae-seq/01-Assembly-data/08-Referee/Mesembriomys-gouldii-ABTC7449/Mesembriomys-gouldii-ABTC7449-referee-corrected.fa -I /mnt/beegfs/gt156213e/murinae-seq/01-Assembly-data/09-Remap/Mesembriomys-gouldii-ABTC7449/Mesembriomys-gouldii-ABTC7449.bam -stand-call-conf 30 --native-pair-hmm-threads 4 -O /mnt/beegfs/gt156213e/murinae-seq/01-Assembly-data/10-Varcall/Mesembriomys-gouldii-ABTC7449/Mesembriomys-gouldii-ABTC7449.vcf &> /mnt/beegfs/gt156213e/murinae-seq/01-Assembly-scripts/logs/10-Varcall-logs/Mesembriomys-gouldii-ABTC7449-haplotypecaller.log
