bcftools filter -m+ -e "MQ < 30.0 || DP < 5 || DP > 60" -e 'ALT="*"' -s Filter --threads 4 -Oz -o /mnt/beegfs/gt156213e/murinae-seq/01-Assembly-data/10-Varcall/Mesembriomys-gouldii-ABTC7449/Mesembriomys-gouldii-ABTC7449.snps.filter.vcf /mnt/beegfs/gt156213e/murinae-seq/01-Assembly-data/10-Varcall/Mesembriomys-gouldii-ABTC7449/Mesembriomys-gouldii-ABTC7449.snps.vcf &> /mnt/beegfs/gt156213e/murinae-seq/01-Assembly-scripts/logs/10-Varcall-logs/Mesembriomys-gouldii-ABTC7449-filter.log
