#!/bin/bash
gatk HaplotypeCaller -R /mnt/beegfs/gt156213e/murinae-seq/01-Assembly-data/08-Referee/Zyzomys-palatalis-ABTC30744/Zyzomys-palatalis-ABTC30744-referee-corrected.fa -I /mnt/beegfs/gt156213e/murinae-seq/01-Assembly-data/09-Remap/Zyzomys-palatalis-ABTC30744/Zyzomys-palatalis-ABTC30744.bam -stand-call-conf 30 --native-pair-hmm-threads 4 -O /mnt/beegfs/gt156213e/murinae-seq/01-Assembly-data/10-Varcall/Zyzomys-palatalis-ABTC30744/Zyzomys-palatalis-ABTC30744.vcf &> /mnt/beegfs/gt156213e/murinae-seq/01-Assembly-scripts/logs/10-Varcall-logs/Zyzomys-palatalis-ABTC30744-haplotypecaller.log
gatk SelectVariants -V /mnt/beegfs/gt156213e/murinae-seq/01-Assembly-data/10-Varcall/Zyzomys-palatalis-ABTC30744/Zyzomys-palatalis-ABTC30744.vcf -O /mnt/beegfs/gt156213e/murinae-seq/01-Assembly-data/10-Varcall/Zyzomys-palatalis-ABTC30744/Zyzomys-palatalis-ABTC30744.snps.vcf -select-type SNP -xl-select-type MIXED -xl-select-type SYMBOLIC &> /mnt/beegfs/gt156213e/murinae-seq/01-Assembly-scripts/logs/10-Varcall-logs/Zyzomys-palatalis-ABTC30744-selectsnps.log
bcftools filter -m+ -e "MQ < 30.0 || DP < 5 || DP > 60" -e 'ALT="*"' -s Filter --threads 4 -Oz -o /mnt/beegfs/gt156213e/murinae-seq/01-Assembly-data/10-Varcall/Zyzomys-palatalis-ABTC30744/Zyzomys-palatalis-ABTC30744.snps.filter.vcf /mnt/beegfs/gt156213e/murinae-seq/01-Assembly-data/10-Varcall/Zyzomys-palatalis-ABTC30744/Zyzomys-palatalis-ABTC30744.snps.vcf &> /mnt/beegfs/gt156213e/murinae-seq/01-Assembly-scripts/logs/10-Varcall-logs/Zyzomys-palatalis-ABTC30744-filter.log
tabix /mnt/beegfs/gt156213e/murinae-seq/01-Assembly-data/10-Varcall/Zyzomys-palatalis-ABTC30744/Zyzomys-palatalis-ABTC30744.snps.filter.vcf
bcftools consensus -f /mnt/beegfs/gt156213e/murinae-seq/01-Assembly-data/08-Referee/Zyzomys-palatalis-ABTC30744/Zyzomys-palatalis-ABTC30744-referee-corrected.fa -o /mnt/beegfs/gt156213e/murinae-seq/01-Assembly-data/10-Varcall/Zyzomys-palatalis-ABTC30744/Zyzomys-palatalis-ABTC30744iupac-consensus.fa -I -e "FILTER='Filter'" /mnt/beegfs/gt156213e/murinae-seq/01-Assembly-data/10-Varcall/Zyzomys-palatalis-ABTC30744/Zyzomys-palatalis-ABTC30744.snps.filter.vcf &> /mnt/beegfs/gt156213e/murinae-seq/01-Assembly-scripts/logs/10-Varcall-logs/Zyzomys-palatalis-ABTC30744-consensus.log
