#!/bin/bash
samtools merge -r /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Aethomys-hindei/Aethomys-hindei.sorted.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Aethomys-hindei/hiseq4000-paired-1/REC1_CKDL190143343-1a-D708-AK1543_H7275BBXX_L2_.fastp.decon.bam
java -jar ~/bin/picard.jar MarkDuplicates I=/scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Aethomys-hindei/Aethomys-hindei.sorted.bam O=/scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Aethomys-hindei/Aethomys-hindei.sorted.mkdup.bam M=/scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Aethomys-hindei/Aethomys-hindei-mkdup-metrics.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/targets-mm10-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Aethomys-hindei/Aethomys-hindei.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Aethomys-hindei/Aethomys-hindei-target-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Aethomys-hindei/Aethomys-hindei-target-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Aethomys-hindei/Aethomys-hindei-avg-target-depth.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/tiles-mm10-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Aethomys-hindei/Aethomys-hindei.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Aethomys-hindei/Aethomys-hindei-tile-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Aethomys-hindei/Aethomys-hindei-tile-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Aethomys-hindei/Aethomys-hindei-avg-tile-depth.txt
samtools flagstat /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Aethomys-hindei/Aethomys-hindei.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Aethomys-hindei/Aethomys-hindei-flagstats.txt
