#!/bin/bash
samtools merge -r /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Bandicota-indica/Bandicota-indica.sorted.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Bandicota-indica/hiseq4000-paired-1/SM009_CKDL190143344-1a-D705-AK1680_H7275BBXX_L3_.fastp.decon.bam
java -jar ~/bin/picard.jar MarkDuplicates I=/scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Bandicota-indica/Bandicota-indica.sorted.bam O=/scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Bandicota-indica/Bandicota-indica.sorted.mkdup.bam M=/scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Bandicota-indica/Bandicota-indica-mkdup-metrics.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/targets-rnor6-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Bandicota-indica/Bandicota-indica.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Bandicota-indica/Bandicota-indica-target-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Bandicota-indica/Bandicota-indica-target-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Bandicota-indica/Bandicota-indica-avg-target-depth.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/tiles-rnor6-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Bandicota-indica/Bandicota-indica.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Bandicota-indica/Bandicota-indica-tile-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Bandicota-indica/Bandicota-indica-tile-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Bandicota-indica/Bandicota-indica-avg-tile-depth.txt
samtools flagstat /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Bandicota-indica/Bandicota-indica.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Bandicota-indica/Bandicota-indica-flagstats.txt
