#!/bin/bash
samtools merge -r /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Batomys-russatus/Batomys-russatus.sorted.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Batomys-russatus/hiseq4000-paired-2/REC28_CKDL190143345-1a-D706-AK1546_H7275BBXX_L4_.fastp.decon.bam
java -jar ~/bin/picard.jar MarkDuplicates I=/scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Batomys-russatus/Batomys-russatus.sorted.bam O=/scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Batomys-russatus/Batomys-russatus.sorted.mkdup.bam M=/scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Batomys-russatus/Batomys-russatus-mkdup-metrics.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/targets-rnor6-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Batomys-russatus/Batomys-russatus.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Batomys-russatus/Batomys-russatus-target-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Batomys-russatus/Batomys-russatus-target-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Batomys-russatus/Batomys-russatus-avg-target-depth.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/tiles-rnor6-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Batomys-russatus/Batomys-russatus.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Batomys-russatus/Batomys-russatus-tile-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Batomys-russatus/Batomys-russatus-tile-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Batomys-russatus/Batomys-russatus-avg-tile-depth.txt
samtools flagstat /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Batomys-russatus/Batomys-russatus.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Batomys-russatus/Batomys-russatus-flagstats.txt
