#!/bin/bash
samtools merge -r /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Bunomys-andrewsi/Bunomys-andrewsi.sorted.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Bunomys-andrewsi/hiseq-paired/andrewsiiWGA_GATGCC_L008_001.fastp.decon.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Bunomys-andrewsi/hiseq-paired/andrewsii-WGA_GATGCC_L007_001.fastp.decon.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Bunomys-andrewsi/hiseq2500-paired/lane2_GATGCC_001.fastp.decon.bam
java -jar ~/bin/picard.jar MarkDuplicates I=/scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Bunomys-andrewsi/Bunomys-andrewsi.sorted.bam O=/scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Bunomys-andrewsi/Bunomys-andrewsi.sorted.mkdup.bam M=/scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Bunomys-andrewsi/Bunomys-andrewsi-mkdup-metrics.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/targets-rnor6-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Bunomys-andrewsi/Bunomys-andrewsi.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Bunomys-andrewsi/Bunomys-andrewsi-target-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Bunomys-andrewsi/Bunomys-andrewsi-target-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Bunomys-andrewsi/Bunomys-andrewsi-avg-target-depth.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/tiles-rnor6-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Bunomys-andrewsi/Bunomys-andrewsi.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Bunomys-andrewsi/Bunomys-andrewsi-tile-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Bunomys-andrewsi/Bunomys-andrewsi-tile-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Bunomys-andrewsi/Bunomys-andrewsi-avg-tile-depth.txt
samtools flagstat /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Bunomys-andrewsi/Bunomys-andrewsi.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Bunomys-andrewsi/Bunomys-andrewsi-flagstats.txt
