#!/bin/bash
samtools merge -r /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Chiromyscus-chiropus/Chiromyscus-chiropus.sorted.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Chiromyscus-chiropus/hiseq4000-paired-1/SM034_CKDL190143344-1a-D702-AK1680_H7275BBXX_L3_.fastp.decon.bam
java -jar ~/bin/picard.jar MarkDuplicates I=/scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Chiromyscus-chiropus/Chiromyscus-chiropus.sorted.bam O=/scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Chiromyscus-chiropus/Chiromyscus-chiropus.sorted.mkdup.bam M=/scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Chiromyscus-chiropus/Chiromyscus-chiropus-mkdup-metrics.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/targets-mm10-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Chiromyscus-chiropus/Chiromyscus-chiropus.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Chiromyscus-chiropus/Chiromyscus-chiropus-target-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Chiromyscus-chiropus/Chiromyscus-chiropus-target-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Chiromyscus-chiropus/Chiromyscus-chiropus-avg-target-depth.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/tiles-mm10-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Chiromyscus-chiropus/Chiromyscus-chiropus.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Chiromyscus-chiropus/Chiromyscus-chiropus-tile-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Chiromyscus-chiropus/Chiromyscus-chiropus-tile-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Chiromyscus-chiropus/Chiromyscus-chiropus-avg-tile-depth.txt
samtools flagstat /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Chiromyscus-chiropus/Chiromyscus-chiropus.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Chiromyscus-chiropus/Chiromyscus-chiropus-flagstats.txt
