#!/bin/bash
samtools merge -r /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Chiruromys-vates/Chiruromys-vates.sorted.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Chiruromys-vates/hiseq4000-paired-2/SM035_CKDL190143345-1a-D706-AK1680_H7275BBXX_L4_.fastp.decon.bam
java -jar ~/bin/picard.jar MarkDuplicates I=/scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Chiruromys-vates/Chiruromys-vates.sorted.bam O=/scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Chiruromys-vates/Chiruromys-vates.sorted.mkdup.bam M=/scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Chiruromys-vates/Chiruromys-vates-mkdup-metrics.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/targets-mm10-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Chiruromys-vates/Chiruromys-vates.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Chiruromys-vates/Chiruromys-vates-target-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Chiruromys-vates/Chiruromys-vates-target-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Chiruromys-vates/Chiruromys-vates-avg-target-depth.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/tiles-mm10-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Chiruromys-vates/Chiruromys-vates.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Chiruromys-vates/Chiruromys-vates-tile-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Chiruromys-vates/Chiruromys-vates-tile-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Chiruromys-vates/Chiruromys-vates-avg-tile-depth.txt
samtools flagstat /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Chiruromys-vates/Chiruromys-vates.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Chiruromys-vates/Chiruromys-vates-flagstats.txt
