#!/bin/bash
samtools merge -r /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Chrotomys-whiteheadi/Chrotomys-whiteheadi.sorted.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Chrotomys-whiteheadi/hiseq4000-paired-2/REC34_CKDL190143345-1a-D704-AK1780_H7275BBXX_L4_.fastp.decon.bam
java -jar ~/bin/picard.jar MarkDuplicates I=/scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Chrotomys-whiteheadi/Chrotomys-whiteheadi.sorted.bam O=/scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Chrotomys-whiteheadi/Chrotomys-whiteheadi.sorted.mkdup.bam M=/scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Chrotomys-whiteheadi/Chrotomys-whiteheadi-mkdup-metrics.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/targets-rnor6-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Chrotomys-whiteheadi/Chrotomys-whiteheadi.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Chrotomys-whiteheadi/Chrotomys-whiteheadi-target-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Chrotomys-whiteheadi/Chrotomys-whiteheadi-target-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Chrotomys-whiteheadi/Chrotomys-whiteheadi-avg-target-depth.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/tiles-rnor6-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Chrotomys-whiteheadi/Chrotomys-whiteheadi.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Chrotomys-whiteheadi/Chrotomys-whiteheadi-tile-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Chrotomys-whiteheadi/Chrotomys-whiteheadi-tile-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Chrotomys-whiteheadi/Chrotomys-whiteheadi-avg-tile-depth.txt
samtools flagstat /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Chrotomys-whiteheadi/Chrotomys-whiteheadi.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Chrotomys-whiteheadi/Chrotomys-whiteheadi-flagstats.txt
