#!/bin/bash
samtools merge -r /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Coccymys-ruemmleri/Coccymys-ruemmleri.sorted.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Coccymys-ruemmleri/hiseq4000-paired-1/SM033_CKDL190143343-1a-D702-AK1681_H7275BBXX_L2_.fastp.decon.bam
java -jar ~/bin/picard.jar MarkDuplicates I=/scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Coccymys-ruemmleri/Coccymys-ruemmleri.sorted.bam O=/scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Coccymys-ruemmleri/Coccymys-ruemmleri.sorted.mkdup.bam M=/scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Coccymys-ruemmleri/Coccymys-ruemmleri-mkdup-metrics.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/targets-mm10-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Coccymys-ruemmleri/Coccymys-ruemmleri.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Coccymys-ruemmleri/Coccymys-ruemmleri-target-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Coccymys-ruemmleri/Coccymys-ruemmleri-target-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Coccymys-ruemmleri/Coccymys-ruemmleri-avg-target-depth.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/tiles-mm10-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Coccymys-ruemmleri/Coccymys-ruemmleri.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Coccymys-ruemmleri/Coccymys-ruemmleri-tile-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Coccymys-ruemmleri/Coccymys-ruemmleri-tile-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Coccymys-ruemmleri/Coccymys-ruemmleri-avg-tile-depth.txt
samtools flagstat /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Coccymys-ruemmleri/Coccymys-ruemmleri.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Coccymys-ruemmleri/Coccymys-ruemmleri-flagstats.txt
