#!/bin/bash
samtools merge -r /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Crateromys-heaneyi/Crateromys-heaneyi.sorted.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Crateromys-heaneyi/hiseq4000-paired-1/REC4_CKDL190143343-1a-D702-AK1544_H7275BBXX_L2_.fastp.decon.bam
java -jar ~/bin/picard.jar MarkDuplicates I=/scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Crateromys-heaneyi/Crateromys-heaneyi.sorted.bam O=/scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Crateromys-heaneyi/Crateromys-heaneyi.sorted.mkdup.bam M=/scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Crateromys-heaneyi/Crateromys-heaneyi-mkdup-metrics.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/targets-mm10-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Crateromys-heaneyi/Crateromys-heaneyi.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Crateromys-heaneyi/Crateromys-heaneyi-target-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Crateromys-heaneyi/Crateromys-heaneyi-target-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Crateromys-heaneyi/Crateromys-heaneyi-avg-target-depth.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/tiles-mm10-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Crateromys-heaneyi/Crateromys-heaneyi.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Crateromys-heaneyi/Crateromys-heaneyi-tile-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Crateromys-heaneyi/Crateromys-heaneyi-tile-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Crateromys-heaneyi/Crateromys-heaneyi-avg-tile-depth.txt
samtools flagstat /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Crateromys-heaneyi/Crateromys-heaneyi.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Crateromys-heaneyi/Crateromys-heaneyi-flagstats.txt
