#!/bin/bash
samtools merge -r /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Dasymys-montanus/Dasymys-montanus.sorted.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Dasymys-montanus/hiseq4000-paired-1/SM086_CKDL190143343-1a-D704-AK1543_H7275BBXX_L2_.fastp.decon.bam
java -jar ~/bin/picard.jar MarkDuplicates I=/scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Dasymys-montanus/Dasymys-montanus.sorted.bam O=/scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Dasymys-montanus/Dasymys-montanus.sorted.mkdup.bam M=/scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Dasymys-montanus/Dasymys-montanus-mkdup-metrics.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/targets-rnor6-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Dasymys-montanus/Dasymys-montanus.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Dasymys-montanus/Dasymys-montanus-target-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Dasymys-montanus/Dasymys-montanus-target-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Dasymys-montanus/Dasymys-montanus-avg-target-depth.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/tiles-rnor6-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Dasymys-montanus/Dasymys-montanus.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Dasymys-montanus/Dasymys-montanus-tile-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Dasymys-montanus/Dasymys-montanus-tile-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Dasymys-montanus/Dasymys-montanus-avg-tile-depth.txt
samtools flagstat /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Dasymys-montanus/Dasymys-montanus.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Dasymys-montanus/Dasymys-montanus-flagstats.txt
