#!/bin/bash
samtools merge -r /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Grammomys-dryas/Grammomys-dryas.sorted.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Grammomys-dryas/hiseq4000-paired-1/REC8_CKDL190143343-1a-D706-AK1544_H7275BBXX_L2_.fastp.decon.bam
java -jar ~/bin/picard.jar MarkDuplicates I=/scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Grammomys-dryas/Grammomys-dryas.sorted.bam O=/scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Grammomys-dryas/Grammomys-dryas.sorted.mkdup.bam M=/scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Grammomys-dryas/Grammomys-dryas-mkdup-metrics.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/targets-mm10-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Grammomys-dryas/Grammomys-dryas.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Grammomys-dryas/Grammomys-dryas-target-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Grammomys-dryas/Grammomys-dryas-target-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Grammomys-dryas/Grammomys-dryas-avg-target-depth.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/tiles-mm10-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Grammomys-dryas/Grammomys-dryas.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Grammomys-dryas/Grammomys-dryas-tile-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Grammomys-dryas/Grammomys-dryas-tile-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Grammomys-dryas/Grammomys-dryas-avg-tile-depth.txt
samtools flagstat /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Grammomys-dryas/Grammomys-dryas.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Grammomys-dryas/Grammomys-dryas-flagstats.txt
