#!/bin/bash
samtools merge -r /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Hydromys-sp-nov/Hydromys-sp-nov.sorted.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Hydromys-sp-nov/hiseq4000-paired-1/REC9_CKDL190143343-1a-D707-AK1544_H7275BBXX_L2_.fastp.decon.bam
java -jar ~/bin/picard.jar MarkDuplicates I=/scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Hydromys-sp-nov/Hydromys-sp-nov.sorted.bam O=/scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Hydromys-sp-nov/Hydromys-sp-nov.sorted.mkdup.bam M=/scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Hydromys-sp-nov/Hydromys-sp-nov-mkdup-metrics.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/targets-rnor6-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Hydromys-sp-nov/Hydromys-sp-nov.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Hydromys-sp-nov/Hydromys-sp-nov-target-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Hydromys-sp-nov/Hydromys-sp-nov-target-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Hydromys-sp-nov/Hydromys-sp-nov-avg-target-depth.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/tiles-rnor6-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Hydromys-sp-nov/Hydromys-sp-nov.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Hydromys-sp-nov/Hydromys-sp-nov-tile-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Hydromys-sp-nov/Hydromys-sp-nov-tile-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Hydromys-sp-nov/Hydromys-sp-nov-avg-tile-depth.txt
samtools flagstat /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Hydromys-sp-nov/Hydromys-sp-nov.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Hydromys-sp-nov/Hydromys-sp-nov-flagstats.txt
