#!/bin/bash
samtools merge -r /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Kadarsanomys-sodyi/Kadarsanomys-sodyi.sorted.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Kadarsanomys-sodyi/hiseq4000-paired-2/REC_27_CKDL190143345-1a-D705-AK1546_H7275BBXX_L4_.fastp.decon.bam
java -jar ~/bin/picard.jar MarkDuplicates I=/scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Kadarsanomys-sodyi/Kadarsanomys-sodyi.sorted.bam O=/scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Kadarsanomys-sodyi/Kadarsanomys-sodyi.sorted.mkdup.bam M=/scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Kadarsanomys-sodyi/Kadarsanomys-sodyi-mkdup-metrics.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/targets-rnor6-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Kadarsanomys-sodyi/Kadarsanomys-sodyi.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Kadarsanomys-sodyi/Kadarsanomys-sodyi-target-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Kadarsanomys-sodyi/Kadarsanomys-sodyi-target-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Kadarsanomys-sodyi/Kadarsanomys-sodyi-avg-target-depth.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/tiles-rnor6-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Kadarsanomys-sodyi/Kadarsanomys-sodyi.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Kadarsanomys-sodyi/Kadarsanomys-sodyi-tile-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Kadarsanomys-sodyi/Kadarsanomys-sodyi-tile-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Kadarsanomys-sodyi/Kadarsanomys-sodyi-avg-tile-depth.txt
samtools flagstat /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Kadarsanomys-sodyi/Kadarsanomys-sodyi.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Kadarsanomys-sodyi/Kadarsanomys-sodyi-flagstats.txt
