#!/bin/bash
samtools merge -r /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Leggadina-forresti/Leggadina-forresti.sorted.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Leggadina-forresti/seq-run-2/52918_AH7T7NBCX2_CTGCGAC_S1_L001_001.fastp.decon.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Leggadina-forresti/seq-run-2/52918_AH7T7NBCX2_CTGCGAC_S1_L002_001.fastp.decon.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Leggadina-forresti/seq-run-1/52918_AH7T2JBCX2_CTGCGAC_S1_L001_001.fastp.decon.bam
java -jar ~/bin/picard.jar MarkDuplicates I=/scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Leggadina-forresti/Leggadina-forresti.sorted.bam O=/scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Leggadina-forresti/Leggadina-forresti.sorted.mkdup.bam M=/scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Leggadina-forresti/Leggadina-forresti-mkdup-metrics.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/targets-rnor6-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Leggadina-forresti/Leggadina-forresti.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Leggadina-forresti/Leggadina-forresti-target-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Leggadina-forresti/Leggadina-forresti-target-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Leggadina-forresti/Leggadina-forresti-avg-target-depth.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/tiles-rnor6-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Leggadina-forresti/Leggadina-forresti.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Leggadina-forresti/Leggadina-forresti-tile-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Leggadina-forresti/Leggadina-forresti-tile-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Leggadina-forresti/Leggadina-forresti-avg-tile-depth.txt
samtools flagstat /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Leggadina-forresti/Leggadina-forresti.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Leggadina-forresti/Leggadina-forresti-flagstats.txt
