#!/bin/bash
samtools merge -r /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Leggadina-lakedownensis/Leggadina-lakedownensis.sorted.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Leggadina-lakedownensis/seq-run-2/52919_AH7T7NBCX2_ACGTATG_S2_L002_001.fastp.decon.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Leggadina-lakedownensis/seq-run-2/52919_AH7T7NBCX2_ACGTATG_S2_L001_001.fastp.decon.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Leggadina-lakedownensis/seq-run-1/52919_AH7T2JBCX2_ACGTATG_S2_L001_001.fastp.decon.bam
java -jar ~/bin/picard.jar MarkDuplicates I=/scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Leggadina-lakedownensis/Leggadina-lakedownensis.sorted.bam O=/scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Leggadina-lakedownensis/Leggadina-lakedownensis.sorted.mkdup.bam M=/scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Leggadina-lakedownensis/Leggadina-lakedownensis-mkdup-metrics.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/targets-mm10-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Leggadina-lakedownensis/Leggadina-lakedownensis.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Leggadina-lakedownensis/Leggadina-lakedownensis-target-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Leggadina-lakedownensis/Leggadina-lakedownensis-target-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Leggadina-lakedownensis/Leggadina-lakedownensis-avg-target-depth.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/tiles-mm10-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Leggadina-lakedownensis/Leggadina-lakedownensis.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Leggadina-lakedownensis/Leggadina-lakedownensis-tile-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Leggadina-lakedownensis/Leggadina-lakedownensis-tile-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Leggadina-lakedownensis/Leggadina-lakedownensis-avg-tile-depth.txt
samtools flagstat /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Leggadina-lakedownensis/Leggadina-lakedownensis.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Leggadina-lakedownensis/Leggadina-lakedownensis-flagstats.txt
