#!/bin/bash
samtools merge -r /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Lenomys-meyeri/Lenomys-meyeri.sorted.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Lenomys-meyeri/hiseq4000-paired-1/REC11_CKDL190143344-1a-D709-AK1544_H7275BBXX_L3_.fastp.decon.bam
java -jar ~/bin/picard.jar MarkDuplicates I=/scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Lenomys-meyeri/Lenomys-meyeri.sorted.bam O=/scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Lenomys-meyeri/Lenomys-meyeri.sorted.mkdup.bam M=/scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Lenomys-meyeri/Lenomys-meyeri-mkdup-metrics.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/targets-mm10-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Lenomys-meyeri/Lenomys-meyeri.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Lenomys-meyeri/Lenomys-meyeri-target-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Lenomys-meyeri/Lenomys-meyeri-target-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Lenomys-meyeri/Lenomys-meyeri-avg-target-depth.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/tiles-mm10-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Lenomys-meyeri/Lenomys-meyeri.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Lenomys-meyeri/Lenomys-meyeri-tile-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Lenomys-meyeri/Lenomys-meyeri-tile-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Lenomys-meyeri/Lenomys-meyeri-avg-tile-depth.txt
samtools flagstat /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Lenomys-meyeri/Lenomys-meyeri.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Lenomys-meyeri/Lenomys-meyeri-flagstats.txt
