#!/bin/bash
samtools merge -r /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Mallomys-rothschildi/Mallomys-rothschildi.sorted.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Mallomys-rothschildi/hiseq4000-paired-2/SM046_CKDL190143345-1a-D710-AK1680_H7275BBXX_L4_.fastp.decon.bam
java -jar ~/bin/picard.jar MarkDuplicates I=/scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Mallomys-rothschildi/Mallomys-rothschildi.sorted.bam O=/scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Mallomys-rothschildi/Mallomys-rothschildi.sorted.mkdup.bam M=/scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Mallomys-rothschildi/Mallomys-rothschildi-mkdup-metrics.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/targets-rnor6-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Mallomys-rothschildi/Mallomys-rothschildi.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Mallomys-rothschildi/Mallomys-rothschildi-target-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Mallomys-rothschildi/Mallomys-rothschildi-target-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Mallomys-rothschildi/Mallomys-rothschildi-avg-target-depth.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/tiles-rnor6-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Mallomys-rothschildi/Mallomys-rothschildi.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Mallomys-rothschildi/Mallomys-rothschildi-tile-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Mallomys-rothschildi/Mallomys-rothschildi-tile-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Mallomys-rothschildi/Mallomys-rothschildi-avg-tile-depth.txt
samtools flagstat /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Mallomys-rothschildi/Mallomys-rothschildi.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Mallomys-rothschildi/Mallomys-rothschildi-flagstats.txt
