#!/bin/bash
samtools merge -r /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Mammelomys-lanosus/Mammelomys-lanosus.sorted.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Mammelomys-lanosus/hiseq4000-paired-1/SM047_CKDL190143343-1a-DY0088-AK1680_H7275BBXX_L2_.fastp.decon.bam
java -jar ~/bin/picard.jar MarkDuplicates I=/scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Mammelomys-lanosus/Mammelomys-lanosus.sorted.bam O=/scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Mammelomys-lanosus/Mammelomys-lanosus.sorted.mkdup.bam M=/scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Mammelomys-lanosus/Mammelomys-lanosus-mkdup-metrics.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/targets-mm10-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Mammelomys-lanosus/Mammelomys-lanosus.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Mammelomys-lanosus/Mammelomys-lanosus-target-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Mammelomys-lanosus/Mammelomys-lanosus-target-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Mammelomys-lanosus/Mammelomys-lanosus-avg-target-depth.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/tiles-mm10-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Mammelomys-lanosus/Mammelomys-lanosus.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Mammelomys-lanosus/Mammelomys-lanosus-tile-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Mammelomys-lanosus/Mammelomys-lanosus-tile-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Mammelomys-lanosus/Mammelomys-lanosus-avg-tile-depth.txt
samtools flagstat /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Mammelomys-lanosus/Mammelomys-lanosus.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Mammelomys-lanosus/Mammelomys-lanosus-flagstats.txt
