#!/bin/bash
samtools merge -r /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Margaretamys-christinae/Margaretamys-christinae.sorted.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Margaretamys-christinae/hiseq4000-paired-2/SM061_CKDL190143345-1a-D702-AK1780_H7275BBXX_L4_.fastp.decon.bam
java -jar ~/bin/picard.jar MarkDuplicates I=/scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Margaretamys-christinae/Margaretamys-christinae.sorted.bam O=/scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Margaretamys-christinae/Margaretamys-christinae.sorted.mkdup.bam M=/scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Margaretamys-christinae/Margaretamys-christinae-mkdup-metrics.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/targets-mm10-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Margaretamys-christinae/Margaretamys-christinae.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Margaretamys-christinae/Margaretamys-christinae-target-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Margaretamys-christinae/Margaretamys-christinae-target-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Margaretamys-christinae/Margaretamys-christinae-avg-target-depth.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/tiles-mm10-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Margaretamys-christinae/Margaretamys-christinae.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Margaretamys-christinae/Margaretamys-christinae-tile-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Margaretamys-christinae/Margaretamys-christinae-tile-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Margaretamys-christinae/Margaretamys-christinae-avg-tile-depth.txt
samtools flagstat /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Margaretamys-christinae/Margaretamys-christinae.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Margaretamys-christinae/Margaretamys-christinae-flagstats.txt
