#!/bin/bash
samtools merge -r /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Maxomys-musschenbroekii/Maxomys-musschenbroekii.sorted.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Maxomys-musschenbroekii/hiseq-paired/musschenbroekii-WGA_CAATTA_L007_001.fastp.decon.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Maxomys-musschenbroekii/hiseq-paired/musschenbroekiiWGA_CAATTA_L008_001.fastp.decon.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Maxomys-musschenbroekii/hiseq2500-paired/lane2_CAATTA_001.fastp.decon.bam
java -jar ~/bin/picard.jar MarkDuplicates I=/scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Maxomys-musschenbroekii/Maxomys-musschenbroekii.sorted.bam O=/scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Maxomys-musschenbroekii/Maxomys-musschenbroekii.sorted.mkdup.bam M=/scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Maxomys-musschenbroekii/Maxomys-musschenbroekii-mkdup-metrics.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/targets-rnor6-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Maxomys-musschenbroekii/Maxomys-musschenbroekii.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Maxomys-musschenbroekii/Maxomys-musschenbroekii-target-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Maxomys-musschenbroekii/Maxomys-musschenbroekii-target-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Maxomys-musschenbroekii/Maxomys-musschenbroekii-avg-target-depth.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/tiles-rnor6-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Maxomys-musschenbroekii/Maxomys-musschenbroekii.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Maxomys-musschenbroekii/Maxomys-musschenbroekii-tile-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Maxomys-musschenbroekii/Maxomys-musschenbroekii-tile-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Maxomys-musschenbroekii/Maxomys-musschenbroekii-avg-tile-depth.txt
samtools flagstat /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Maxomys-musschenbroekii/Maxomys-musschenbroekii.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Maxomys-musschenbroekii/Maxomys-musschenbroekii-flagstats.txt
