#!/bin/bash
samtools merge -r /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Mesembriomys-gouldii/Mesembriomys-gouldii.sorted.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Mesembriomys-gouldii/seq-run-2/53919_HTVLWBCX2_CTCGATG_S7_L001_001.fastp.decon.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Mesembriomys-gouldii/seq-run-2/53919_HTVLWBCX2_CTCGATG_S7_L002_001.fastp.decon.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Mesembriomys-gouldii/seq-run-1/53919_HTVLKBCX2_CTCGATG_S7_L002_001.fastp.decon.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Mesembriomys-gouldii/seq-run-1/53919_HTVLKBCX2_CTCGATG_S7_L001_001.fastp.decon.bam
java -jar ~/bin/picard.jar MarkDuplicates I=/scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Mesembriomys-gouldii/Mesembriomys-gouldii.sorted.bam O=/scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Mesembriomys-gouldii/Mesembriomys-gouldii.sorted.mkdup.bam M=/scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Mesembriomys-gouldii/Mesembriomys-gouldii-mkdup-metrics.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/targets-rnor6-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Mesembriomys-gouldii/Mesembriomys-gouldii.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Mesembriomys-gouldii/Mesembriomys-gouldii-target-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Mesembriomys-gouldii/Mesembriomys-gouldii-target-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Mesembriomys-gouldii/Mesembriomys-gouldii-avg-target-depth.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/tiles-rnor6-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Mesembriomys-gouldii/Mesembriomys-gouldii.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Mesembriomys-gouldii/Mesembriomys-gouldii-tile-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Mesembriomys-gouldii/Mesembriomys-gouldii-tile-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Mesembriomys-gouldii/Mesembriomys-gouldii-avg-tile-depth.txt
samtools flagstat /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Mesembriomys-gouldii/Mesembriomys-gouldii.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Mesembriomys-gouldii/Mesembriomys-gouldii-flagstats.txt
