#!/bin/bash
samtools merge -r /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Mesembriomys-macrurus/Mesembriomys-macrurus.sorted.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Mesembriomys-macrurus/seq-run-2/53918_HTVLWBCX2_ATGGAGA_S6_L002_001.fastp.decon.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Mesembriomys-macrurus/seq-run-2/53918_HTVLWBCX2_ATGGAGA_S6_L001_001.fastp.decon.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Mesembriomys-macrurus/seq-run-1/53918_HTVLKBCX2_ATGGAGA_S6_L001_001.fastp.decon.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Mesembriomys-macrurus/seq-run-1/53918_HTVLKBCX2_ATGGAGA_S6_L002_001.fastp.decon.bam
java -jar ~/bin/picard.jar MarkDuplicates I=/scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Mesembriomys-macrurus/Mesembriomys-macrurus.sorted.bam O=/scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Mesembriomys-macrurus/Mesembriomys-macrurus.sorted.mkdup.bam M=/scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Mesembriomys-macrurus/Mesembriomys-macrurus-mkdup-metrics.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/targets-rnor6-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Mesembriomys-macrurus/Mesembriomys-macrurus.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Mesembriomys-macrurus/Mesembriomys-macrurus-target-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Mesembriomys-macrurus/Mesembriomys-macrurus-target-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Mesembriomys-macrurus/Mesembriomys-macrurus-avg-target-depth.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/tiles-rnor6-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Mesembriomys-macrurus/Mesembriomys-macrurus.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Mesembriomys-macrurus/Mesembriomys-macrurus-tile-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Mesembriomys-macrurus/Mesembriomys-macrurus-tile-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Mesembriomys-macrurus/Mesembriomys-macrurus-avg-tile-depth.txt
samtools flagstat /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Mesembriomys-macrurus/Mesembriomys-macrurus.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Mesembriomys-macrurus/Mesembriomys-macrurus-flagstats.txt
