#!/bin/bash
samtools merge -r /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Mirzamys-louiseae/Mirzamys-louiseae.sorted.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Mirzamys-louiseae/hiseq4000-paired-1/SM051_CKDL190143344-1a-D706-AK1681_H7275BBXX_L3_.fastp.decon.bam
java -jar ~/bin/picard.jar MarkDuplicates I=/scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Mirzamys-louiseae/Mirzamys-louiseae.sorted.bam O=/scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Mirzamys-louiseae/Mirzamys-louiseae.sorted.mkdup.bam M=/scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Mirzamys-louiseae/Mirzamys-louiseae-mkdup-metrics.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/targets-mm10-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Mirzamys-louiseae/Mirzamys-louiseae.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Mirzamys-louiseae/Mirzamys-louiseae-target-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Mirzamys-louiseae/Mirzamys-louiseae-target-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Mirzamys-louiseae/Mirzamys-louiseae-avg-target-depth.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/tiles-mm10-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Mirzamys-louiseae/Mirzamys-louiseae.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Mirzamys-louiseae/Mirzamys-louiseae-tile-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Mirzamys-louiseae/Mirzamys-louiseae-tile-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Mirzamys-louiseae/Mirzamys-louiseae-avg-tile-depth.txt
samtools flagstat /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Mirzamys-louiseae/Mirzamys-louiseae.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Mirzamys-louiseae/Mirzamys-louiseae-flagstats.txt
