#!/bin/bash
samtools merge -r /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Mus-molossinus/Mus-molossinus.sorted.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Mus-molossinus/hiseq2000-paired-2/lane1_GCTCGA_L001_001.fastp.decon.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Mus-molossinus/hiseq2000-paired-1/10460X8_131030_SN141_0734_AC2RLAACXX_7_.fastp.decon.bam
java -jar ~/bin/picard.jar MarkDuplicates I=/scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Mus-molossinus/Mus-molossinus.sorted.bam O=/scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Mus-molossinus/Mus-molossinus.sorted.mkdup.bam M=/scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Mus-molossinus/Mus-molossinus-mkdup-metrics.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/targets-mm10-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Mus-molossinus/Mus-molossinus.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Mus-molossinus/Mus-molossinus-target-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Mus-molossinus/Mus-molossinus-target-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Mus-molossinus/Mus-molossinus-avg-target-depth.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/tiles-mm10-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Mus-molossinus/Mus-molossinus.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Mus-molossinus/Mus-molossinus-tile-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Mus-molossinus/Mus-molossinus-tile-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Mus-molossinus/Mus-molossinus-avg-tile-depth.txt
samtools flagstat /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Mus-molossinus/Mus-molossinus.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Mus-molossinus/Mus-molossinus-flagstats.txt
