#!/bin/bash
samtools merge -r /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Mus-musculus-musculus/Mus-musculus-musculus.sorted.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Mus-musculus-musculus/hiseq2000-paired-2/lane1_TTGGAT_L001_001.fastp.decon.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Mus-musculus-musculus/hiseq2000-paired-1/10460X14_131030_SN141_0734_AC2RLAACXX_7_.fastp.decon.bam
java -jar ~/bin/picard.jar MarkDuplicates I=/scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Mus-musculus-musculus/Mus-musculus-musculus.sorted.bam O=/scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Mus-musculus-musculus/Mus-musculus-musculus.sorted.mkdup.bam M=/scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Mus-musculus-musculus/Mus-musculus-musculus-mkdup-metrics.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/targets-rnor6-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Mus-musculus-musculus/Mus-musculus-musculus.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Mus-musculus-musculus/Mus-musculus-musculus-target-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Mus-musculus-musculus/Mus-musculus-musculus-target-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Mus-musculus-musculus/Mus-musculus-musculus-avg-target-depth.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/tiles-rnor6-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Mus-musculus-musculus/Mus-musculus-musculus.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Mus-musculus-musculus/Mus-musculus-musculus-tile-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Mus-musculus-musculus/Mus-musculus-musculus-tile-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Mus-musculus-musculus/Mus-musculus-musculus-avg-tile-depth.txt
samtools flagstat /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Mus-musculus-musculus/Mus-musculus-musculus.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Mus-musculus-musculus/Mus-musculus-musculus-flagstats.txt
