#!/bin/bash
samtools merge -r /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Mus-pos_ctrl_B/Mus-pos_ctrl_B.sorted.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Mus-pos_ctrl_B/hiseq4000-paired-1/M44_B11_CKDL190143344-1a-D711-AK1681_H7275BBXX_L3_.fastp.decon.bam
java -jar ~/bin/picard.jar MarkDuplicates I=/scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Mus-pos_ctrl_B/Mus-pos_ctrl_B.sorted.bam O=/scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Mus-pos_ctrl_B/Mus-pos_ctrl_B.sorted.mkdup.bam M=/scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Mus-pos_ctrl_B/Mus-pos_ctrl_B-mkdup-metrics.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/targets-mm10-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Mus-pos_ctrl_B/Mus-pos_ctrl_B.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Mus-pos_ctrl_B/Mus-pos_ctrl_B-target-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Mus-pos_ctrl_B/Mus-pos_ctrl_B-target-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Mus-pos_ctrl_B/Mus-pos_ctrl_B-avg-target-depth.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/tiles-mm10-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Mus-pos_ctrl_B/Mus-pos_ctrl_B.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Mus-pos_ctrl_B/Mus-pos_ctrl_B-tile-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Mus-pos_ctrl_B/Mus-pos_ctrl_B-tile-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Mus-pos_ctrl_B/Mus-pos_ctrl_B-avg-tile-depth.txt
samtools flagstat /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Mus-pos_ctrl_B/Mus-pos_ctrl_B.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Mus-pos_ctrl_B/Mus-pos_ctrl_B-flagstats.txt
