#!/bin/bash
samtools merge -r /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Mus-pos_ctrl_D2/Mus-pos_ctrl_D2.sorted.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Mus-pos_ctrl_D2/hiseq4000-paired-2/M44_D11_CKDL190143345-1a-D711-AK1780_H7275BBXX_L4_.fastp.decon.bam
java -jar ~/bin/picard.jar MarkDuplicates I=/scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Mus-pos_ctrl_D2/Mus-pos_ctrl_D2.sorted.bam O=/scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Mus-pos_ctrl_D2/Mus-pos_ctrl_D2.sorted.mkdup.bam M=/scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Mus-pos_ctrl_D2/Mus-pos_ctrl_D2-mkdup-metrics.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/targets-rnor6-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Mus-pos_ctrl_D2/Mus-pos_ctrl_D2.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Mus-pos_ctrl_D2/Mus-pos_ctrl_D2-target-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Mus-pos_ctrl_D2/Mus-pos_ctrl_D2-target-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Mus-pos_ctrl_D2/Mus-pos_ctrl_D2-avg-target-depth.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/tiles-rnor6-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Mus-pos_ctrl_D2/Mus-pos_ctrl_D2.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Mus-pos_ctrl_D2/Mus-pos_ctrl_D2-tile-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Mus-pos_ctrl_D2/Mus-pos_ctrl_D2-tile-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Mus-pos_ctrl_D2/Mus-pos_ctrl_D2-avg-tile-depth.txt
samtools flagstat /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Mus-pos_ctrl_D2/Mus-pos_ctrl_D2.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Mus-pos_ctrl_D2/Mus-pos_ctrl_D2-flagstats.txt
