#!/bin/bash
samtools merge -r /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Mus-pos_ctrl_E/Mus-pos_ctrl_E.sorted.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Mus-pos_ctrl_E/hiseq4000-paired-1/M44_E11_CKDL190143343-1a-D711-AK1543_H7275BBXX_L2_.fastp.decon.bam
java -jar ~/bin/picard.jar MarkDuplicates I=/scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Mus-pos_ctrl_E/Mus-pos_ctrl_E.sorted.bam O=/scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Mus-pos_ctrl_E/Mus-pos_ctrl_E.sorted.mkdup.bam M=/scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Mus-pos_ctrl_E/Mus-pos_ctrl_E-mkdup-metrics.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/targets-mm10-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Mus-pos_ctrl_E/Mus-pos_ctrl_E.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Mus-pos_ctrl_E/Mus-pos_ctrl_E-target-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Mus-pos_ctrl_E/Mus-pos_ctrl_E-target-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Mus-pos_ctrl_E/Mus-pos_ctrl_E-avg-target-depth.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/tiles-mm10-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Mus-pos_ctrl_E/Mus-pos_ctrl_E.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Mus-pos_ctrl_E/Mus-pos_ctrl_E-tile-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Mus-pos_ctrl_E/Mus-pos_ctrl_E-tile-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Mus-pos_ctrl_E/Mus-pos_ctrl_E-avg-tile-depth.txt
samtools flagstat /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Mus-pos_ctrl_E/Mus-pos_ctrl_E.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Mus-pos_ctrl_E/Mus-pos_ctrl_E-flagstats.txt
