#!/bin/bash
samtools merge -r /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Mus-spicilegus/Mus-spicilegus.sorted.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Mus-spicilegus/hiseq2000-paired-2/lane1_AACTCC_L001_001.fastp.decon.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Mus-spicilegus/hiseq2000-paired-1/10460X11_131030_SN141_0734_AC2RLAACXX_7_.fastp.decon.bam
java -jar ~/bin/picard.jar MarkDuplicates I=/scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Mus-spicilegus/Mus-spicilegus.sorted.bam O=/scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Mus-spicilegus/Mus-spicilegus.sorted.mkdup.bam M=/scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Mus-spicilegus/Mus-spicilegus-mkdup-metrics.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/targets-mm10-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Mus-spicilegus/Mus-spicilegus.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Mus-spicilegus/Mus-spicilegus-target-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Mus-spicilegus/Mus-spicilegus-target-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Mus-spicilegus/Mus-spicilegus-avg-target-depth.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/tiles-mm10-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Mus-spicilegus/Mus-spicilegus.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Mus-spicilegus/Mus-spicilegus-tile-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Mus-spicilegus/Mus-spicilegus-tile-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Mus-spicilegus/Mus-spicilegus-avg-tile-depth.txt
samtools flagstat /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Mus-spicilegus/Mus-spicilegus.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Mus-spicilegus/Mus-spicilegus-flagstats.txt
