#!/bin/bash
samtools merge -r /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Musseromys-anacuao/Musseromys-anacuao.sorted.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Musseromys-anacuao/hiseq4000-paired-1/SM063_CKDL190143343-1a-D707-AK1681_H7275BBXX_L2_.fastp.decon.bam
java -jar ~/bin/picard.jar MarkDuplicates I=/scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Musseromys-anacuao/Musseromys-anacuao.sorted.bam O=/scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Musseromys-anacuao/Musseromys-anacuao.sorted.mkdup.bam M=/scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Musseromys-anacuao/Musseromys-anacuao-mkdup-metrics.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/targets-rnor6-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Musseromys-anacuao/Musseromys-anacuao.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Musseromys-anacuao/Musseromys-anacuao-target-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Musseromys-anacuao/Musseromys-anacuao-target-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Musseromys-anacuao/Musseromys-anacuao-avg-target-depth.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/tiles-rnor6-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Musseromys-anacuao/Musseromys-anacuao.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Musseromys-anacuao/Musseromys-anacuao-tile-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Musseromys-anacuao/Musseromys-anacuao-tile-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Musseromys-anacuao/Musseromys-anacuao-avg-tile-depth.txt
samtools flagstat /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Musseromys-anacuao/Musseromys-anacuao.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Musseromys-anacuao/Musseromys-anacuao-flagstats.txt
