#!/bin/bash
samtools merge -r /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Nesokia-indica/Nesokia-indica.sorted.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Nesokia-indica/hiseq4000-paired-2/SM053_CKDL190143345-1a-D701-AK1682_H7275BBXX_L4_.fastp.decon.bam
java -jar ~/bin/picard.jar MarkDuplicates I=/scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Nesokia-indica/Nesokia-indica.sorted.bam O=/scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Nesokia-indica/Nesokia-indica.sorted.mkdup.bam M=/scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Nesokia-indica/Nesokia-indica-mkdup-metrics.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/targets-rnor6-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Nesokia-indica/Nesokia-indica.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Nesokia-indica/Nesokia-indica-target-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Nesokia-indica/Nesokia-indica-target-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Nesokia-indica/Nesokia-indica-avg-target-depth.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/tiles-rnor6-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Nesokia-indica/Nesokia-indica.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Nesokia-indica/Nesokia-indica-tile-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Nesokia-indica/Nesokia-indica-tile-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Nesokia-indica/Nesokia-indica-avg-tile-depth.txt
samtools flagstat /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Nesokia-indica/Nesokia-indica.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Nesokia-indica/Nesokia-indica-flagstats.txt
