#!/bin/bash
samtools merge -r /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Notomys-alexis/Notomys-alexis.sorted.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Notomys-alexis/seq-run-2/52920_AH7T7NBCX2_ATACTGA_S3_L001_001.fastp.decon.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Notomys-alexis/seq-run-2/52920_AH7T7NBCX2_ATACTGA_S3_L002_001.fastp.decon.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Notomys-alexis/seq-run-1/52920_AH7T2JBCX2_ATACTGA_S3_L001_001.fastp.decon.bam
java -jar ~/bin/picard.jar MarkDuplicates I=/scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Notomys-alexis/Notomys-alexis.sorted.bam O=/scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Notomys-alexis/Notomys-alexis.sorted.mkdup.bam M=/scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Notomys-alexis/Notomys-alexis-mkdup-metrics.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/targets-mm10-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Notomys-alexis/Notomys-alexis.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Notomys-alexis/Notomys-alexis-target-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Notomys-alexis/Notomys-alexis-target-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Notomys-alexis/Notomys-alexis-avg-target-depth.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/tiles-mm10-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Notomys-alexis/Notomys-alexis.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Notomys-alexis/Notomys-alexis-tile-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Notomys-alexis/Notomys-alexis-tile-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Notomys-alexis/Notomys-alexis-avg-tile-depth.txt
samtools flagstat /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Notomys-alexis/Notomys-alexis.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Notomys-alexis/Notomys-alexis-flagstats.txt
