#!/bin/bash
samtools merge -r /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Notomys-amplus/Notomys-amplus.sorted.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Notomys-amplus/seq-run-2/53926_HTVLWBCX2_TTGGATC_S14_L002_001.fastp.decon.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Notomys-amplus/seq-run-2/53926_HTVLWBCX2_TTGGATC_S14_L001_001.fastp.decon.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Notomys-amplus/seq-run-1/53926_HTVLKBCX2_TTGGATC_S14_L002_001.fastp.decon.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Notomys-amplus/seq-run-1/53926_HTVLKBCX2_TTGGATC_S14_L001_001.fastp.decon.bam
java -jar ~/bin/picard.jar MarkDuplicates I=/scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Notomys-amplus/Notomys-amplus.sorted.bam O=/scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Notomys-amplus/Notomys-amplus.sorted.mkdup.bam M=/scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Notomys-amplus/Notomys-amplus-mkdup-metrics.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/targets-mm10-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Notomys-amplus/Notomys-amplus.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Notomys-amplus/Notomys-amplus-target-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Notomys-amplus/Notomys-amplus-target-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Notomys-amplus/Notomys-amplus-avg-target-depth.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/tiles-mm10-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Notomys-amplus/Notomys-amplus.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Notomys-amplus/Notomys-amplus-tile-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Notomys-amplus/Notomys-amplus-tile-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Notomys-amplus/Notomys-amplus-avg-tile-depth.txt
samtools flagstat /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Notomys-amplus/Notomys-amplus.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Notomys-amplus/Notomys-amplus-flagstats.txt
