#!/bin/bash
samtools merge -r /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Notomys-aquilo/Notomys-aquilo.sorted.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Notomys-aquilo/seq-run-2/52921_AH7T7NBCX2_TACTTAG_S4_L001_001.fastp.decon.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Notomys-aquilo/seq-run-2/52921_AH7T7NBCX2_TACTTAG_S4_L002_001.fastp.decon.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Notomys-aquilo/seq-run-1/52921_AH7T2JBCX2_TACTTAG_S4_L001_001.fastp.decon.bam
java -jar ~/bin/picard.jar MarkDuplicates I=/scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Notomys-aquilo/Notomys-aquilo.sorted.bam O=/scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Notomys-aquilo/Notomys-aquilo.sorted.mkdup.bam M=/scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Notomys-aquilo/Notomys-aquilo-mkdup-metrics.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/targets-mm10-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Notomys-aquilo/Notomys-aquilo.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Notomys-aquilo/Notomys-aquilo-target-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Notomys-aquilo/Notomys-aquilo-target-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Notomys-aquilo/Notomys-aquilo-avg-target-depth.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/tiles-mm10-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Notomys-aquilo/Notomys-aquilo.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Notomys-aquilo/Notomys-aquilo-tile-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Notomys-aquilo/Notomys-aquilo-tile-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Notomys-aquilo/Notomys-aquilo-avg-tile-depth.txt
samtools flagstat /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Notomys-aquilo/Notomys-aquilo.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Notomys-aquilo/Notomys-aquilo-flagstats.txt
