#!/bin/bash
samtools merge -r /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Notomys-fuscus/Notomys-fuscus.sorted.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Notomys-fuscus/seq-run-2/52924_AH7T7NBCX2_AGAAGAC_S7_L001_001.fastp.decon.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Notomys-fuscus/seq-run-2/52924_AH7T7NBCX2_AGAAGAC_S7_L002_001.fastp.decon.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Notomys-fuscus/seq-run-1/52924_AH7T2JBCX2_AGAAGAC_S7_L001_001.fastp.decon.bam
java -jar ~/bin/picard.jar MarkDuplicates I=/scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Notomys-fuscus/Notomys-fuscus.sorted.bam O=/scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Notomys-fuscus/Notomys-fuscus.sorted.mkdup.bam M=/scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Notomys-fuscus/Notomys-fuscus-mkdup-metrics.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/targets-rnor6-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Notomys-fuscus/Notomys-fuscus.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Notomys-fuscus/Notomys-fuscus-target-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Notomys-fuscus/Notomys-fuscus-target-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Notomys-fuscus/Notomys-fuscus-avg-target-depth.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/tiles-rnor6-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Notomys-fuscus/Notomys-fuscus.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Notomys-fuscus/Notomys-fuscus-tile-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Notomys-fuscus/Notomys-fuscus-tile-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Notomys-fuscus/Notomys-fuscus-avg-tile-depth.txt
samtools flagstat /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Notomys-fuscus/Notomys-fuscus.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Notomys-fuscus/Notomys-fuscus-flagstats.txt
