#!/bin/bash
samtools merge -r /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Notomys-longicaudatus/Notomys-longicaudatus.sorted.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Notomys-longicaudatus/seq-run-2/52956_BH7T77BCX2_GGTCAAG_S19_L001_001.fastp.decon.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Notomys-longicaudatus/seq-run-2/52956_BH7T77BCX2_GGTCAAG_S19_L002_001.fastp.decon.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Notomys-longicaudatus/seq-run-1/52956_BH7T33BCX2_GGTCAAG_S19_L001_001.fastp.decon.bam
java -jar ~/bin/picard.jar MarkDuplicates I=/scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Notomys-longicaudatus/Notomys-longicaudatus.sorted.bam O=/scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Notomys-longicaudatus/Notomys-longicaudatus.sorted.mkdup.bam M=/scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Notomys-longicaudatus/Notomys-longicaudatus-mkdup-metrics.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/targets-rnor6-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Notomys-longicaudatus/Notomys-longicaudatus.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Notomys-longicaudatus/Notomys-longicaudatus-target-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Notomys-longicaudatus/Notomys-longicaudatus-target-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Notomys-longicaudatus/Notomys-longicaudatus-avg-target-depth.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/tiles-rnor6-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Notomys-longicaudatus/Notomys-longicaudatus.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Notomys-longicaudatus/Notomys-longicaudatus-tile-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Notomys-longicaudatus/Notomys-longicaudatus-tile-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Notomys-longicaudatus/Notomys-longicaudatus-avg-tile-depth.txt
samtools flagstat /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Notomys-longicaudatus/Notomys-longicaudatus.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Notomys-longicaudatus/Notomys-longicaudatus-flagstats.txt
