#!/bin/bash
samtools merge -r /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Notomys-macrotis/Notomys-macrotis.sorted.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Notomys-macrotis/seq-run-2/53929_HTVLWBCX2_AGGTACC_S17_L001_001.fastp.decon.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Notomys-macrotis/seq-run-2/53929_HTVLWBCX2_AGGTACC_S17_L002_001.fastp.decon.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Notomys-macrotis/seq-run-1/53929_HTVLKBCX2_AGGTACC_S17_L001_001.fastp.decon.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Notomys-macrotis/seq-run-1/53929_HTVLKBCX2_AGGTACC_S17_L002_001.fastp.decon.bam
java -jar ~/bin/picard.jar MarkDuplicates I=/scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Notomys-macrotis/Notomys-macrotis.sorted.bam O=/scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Notomys-macrotis/Notomys-macrotis.sorted.mkdup.bam M=/scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Notomys-macrotis/Notomys-macrotis-mkdup-metrics.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/targets-mm10-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Notomys-macrotis/Notomys-macrotis.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Notomys-macrotis/Notomys-macrotis-target-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Notomys-macrotis/Notomys-macrotis-target-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Notomys-macrotis/Notomys-macrotis-avg-target-depth.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/tiles-mm10-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Notomys-macrotis/Notomys-macrotis.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Notomys-macrotis/Notomys-macrotis-tile-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Notomys-macrotis/Notomys-macrotis-tile-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Notomys-macrotis/Notomys-macrotis-avg-tile-depth.txt
samtools flagstat /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Notomys-macrotis/Notomys-macrotis.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Notomys-macrotis/Notomys-macrotis-flagstats.txt
