#!/bin/bash
samtools merge -r /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Notomys-mitchellii/Notomys-mitchellii.sorted.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Notomys-mitchellii/seq-run-2/53916_HTVLWBCX2_GGATCAA_S4_L002_001.fastp.decon.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Notomys-mitchellii/seq-run-2/53916_HTVLWBCX2_GGATCAA_S4_L001_001.fastp.decon.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Notomys-mitchellii/seq-run-1/53916_HTVLKBCX2_GGATCAA_S4_L002_001.fastp.decon.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Notomys-mitchellii/seq-run-1/53916_HTVLKBCX2_GGATCAA_S4_L001_001.fastp.decon.bam
java -jar ~/bin/picard.jar MarkDuplicates I=/scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Notomys-mitchellii/Notomys-mitchellii.sorted.bam O=/scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Notomys-mitchellii/Notomys-mitchellii.sorted.mkdup.bam M=/scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Notomys-mitchellii/Notomys-mitchellii-mkdup-metrics.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/targets-rnor6-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Notomys-mitchellii/Notomys-mitchellii.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Notomys-mitchellii/Notomys-mitchellii-target-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Notomys-mitchellii/Notomys-mitchellii-target-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Notomys-mitchellii/Notomys-mitchellii-avg-target-depth.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/tiles-rnor6-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Notomys-mitchellii/Notomys-mitchellii.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Notomys-mitchellii/Notomys-mitchellii-tile-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Notomys-mitchellii/Notomys-mitchellii-tile-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Notomys-mitchellii/Notomys-mitchellii-avg-tile-depth.txt
samtools flagstat /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Notomys-mitchellii/Notomys-mitchellii.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Notomys-mitchellii/Notomys-mitchellii-flagstats.txt
