#!/bin/bash
samtools merge -r /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Otomys-tropicalis/Otomys-tropicalis.sorted.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Otomys-tropicalis/hiseq4000-paired-1/REC15_CKDL190143344-1a-DY0088-AK1545_H7275BBXX_L3_.fastp.decon.bam
java -jar ~/bin/picard.jar MarkDuplicates I=/scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Otomys-tropicalis/Otomys-tropicalis.sorted.bam O=/scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Otomys-tropicalis/Otomys-tropicalis.sorted.mkdup.bam M=/scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Otomys-tropicalis/Otomys-tropicalis-mkdup-metrics.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/targets-rnor6-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Otomys-tropicalis/Otomys-tropicalis.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Otomys-tropicalis/Otomys-tropicalis-target-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Otomys-tropicalis/Otomys-tropicalis-target-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Otomys-tropicalis/Otomys-tropicalis-avg-target-depth.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/tiles-rnor6-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Otomys-tropicalis/Otomys-tropicalis.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Otomys-tropicalis/Otomys-tropicalis-tile-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Otomys-tropicalis/Otomys-tropicalis-tile-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Otomys-tropicalis/Otomys-tropicalis-avg-tile-depth.txt
samtools flagstat /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Otomys-tropicalis/Otomys-tropicalis.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Otomys-tropicalis/Otomys-tropicalis-flagstats.txt
