#!/bin/bash
samtools merge -r /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Papagomys-armandvillei/Papagomys-armandvillei.sorted.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Papagomys-armandvillei/hiseq4000-paired-1/SM057_CKDL190143344-1a-D704-AK1682_H7275BBXX_L3_.fastp.decon.bam
java -jar ~/bin/picard.jar MarkDuplicates I=/scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Papagomys-armandvillei/Papagomys-armandvillei.sorted.bam O=/scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Papagomys-armandvillei/Papagomys-armandvillei.sorted.mkdup.bam M=/scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Papagomys-armandvillei/Papagomys-armandvillei-mkdup-metrics.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/targets-mm10-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Papagomys-armandvillei/Papagomys-armandvillei.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Papagomys-armandvillei/Papagomys-armandvillei-target-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Papagomys-armandvillei/Papagomys-armandvillei-target-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Papagomys-armandvillei/Papagomys-armandvillei-avg-target-depth.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/tiles-mm10-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Papagomys-armandvillei/Papagomys-armandvillei.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Papagomys-armandvillei/Papagomys-armandvillei-tile-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Papagomys-armandvillei/Papagomys-armandvillei-tile-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Papagomys-armandvillei/Papagomys-armandvillei-avg-tile-depth.txt
samtools flagstat /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Papagomys-armandvillei/Papagomys-armandvillei.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Papagomys-armandvillei/Papagomys-armandvillei-flagstats.txt
