#!/bin/bash
samtools merge -r /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Pelomys-minor/Pelomys-minor.sorted.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Pelomys-minor/hiseq4000-paired-2/REC16_CKDL190143345-1a-D704-AK1545_H7275BBXX_L4_.fastp.decon.bam
java -jar ~/bin/picard.jar MarkDuplicates I=/scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Pelomys-minor/Pelomys-minor.sorted.bam O=/scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Pelomys-minor/Pelomys-minor.sorted.mkdup.bam M=/scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Pelomys-minor/Pelomys-minor-mkdup-metrics.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/targets-rnor6-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Pelomys-minor/Pelomys-minor.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Pelomys-minor/Pelomys-minor-target-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Pelomys-minor/Pelomys-minor-target-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Pelomys-minor/Pelomys-minor-avg-target-depth.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/tiles-rnor6-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Pelomys-minor/Pelomys-minor.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Pelomys-minor/Pelomys-minor-tile-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Pelomys-minor/Pelomys-minor-tile-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Pelomys-minor/Pelomys-minor-avg-tile-depth.txt
samtools flagstat /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Pelomys-minor/Pelomys-minor.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Pelomys-minor/Pelomys-minor-flagstats.txt
