#!/bin/bash
samtools merge -r /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Phloeomys-pallidus/Phloeomys-pallidus.sorted.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Phloeomys-pallidus/nextseq-single-1/JEC43_S28_L001_R1_001.fastp.decon.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Phloeomys-pallidus/nextseq-single-1/JEC43_S28_L004_R1_001.fastp.decon.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Phloeomys-pallidus/nextseq-single-1/JEC43_S28_L003_R1_001.fastp.decon.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Phloeomys-pallidus/nextseq-single-1/JEC43_S28_L002_R1_001.fastp.decon.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Phloeomys-pallidus/nextseq-paired-2/JEC43_S36_L004_001.fastp.decon.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Phloeomys-pallidus/nextseq-paired-2/JEC43_S36_L002_001.fastp.decon.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Phloeomys-pallidus/nextseq-paired-2/JEC43_S36_L001_001.fastp.decon.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Phloeomys-pallidus/nextseq-paired-2/JEC43_S36_L003_001.fastp.decon.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Phloeomys-pallidus/miseq-paired/JEC43_S5_L001_001.fastp.decon.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Phloeomys-pallidus/nextseq-single-2/JEC43_S28_L001_R1_001.fastp.decon.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Phloeomys-pallidus/nextseq-single-2/JEC43_S28_L004_R1_001.fastp.decon.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Phloeomys-pallidus/nextseq-single-2/JEC43_S28_L003_R1_001.fastp.decon.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Phloeomys-pallidus/nextseq-single-2/JEC43_S28_L002_R1_001.fastp.decon.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Phloeomys-pallidus/nextseq-paired-1/JEC43_S5_001.fastp.decon.bam
java -jar ~/bin/picard.jar MarkDuplicates I=/scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Phloeomys-pallidus/Phloeomys-pallidus.sorted.bam O=/scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Phloeomys-pallidus/Phloeomys-pallidus.sorted.mkdup.bam M=/scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Phloeomys-pallidus/Phloeomys-pallidus-mkdup-metrics.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/targets-mm10-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Phloeomys-pallidus/Phloeomys-pallidus.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Phloeomys-pallidus/Phloeomys-pallidus-target-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Phloeomys-pallidus/Phloeomys-pallidus-target-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Phloeomys-pallidus/Phloeomys-pallidus-avg-target-depth.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/tiles-mm10-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Phloeomys-pallidus/Phloeomys-pallidus.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Phloeomys-pallidus/Phloeomys-pallidus-tile-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Phloeomys-pallidus/Phloeomys-pallidus-tile-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Phloeomys-pallidus/Phloeomys-pallidus-avg-tile-depth.txt
samtools flagstat /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Phloeomys-pallidus/Phloeomys-pallidus.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedMouse/Phloeomys-pallidus/Phloeomys-pallidus-flagstats.txt
