#!/bin/bash
samtools merge -r /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Praomys-jacksoni/Praomys-jacksoni.sorted.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Praomys-jacksoni/hiseq4000-paired-1/REC31_CKDL190143344-1a-D710-AK1546_H7275BBXX_L3_.fastp.decon.bam
java -jar ~/bin/picard.jar MarkDuplicates I=/scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Praomys-jacksoni/Praomys-jacksoni.sorted.bam O=/scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Praomys-jacksoni/Praomys-jacksoni.sorted.mkdup.bam M=/scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Praomys-jacksoni/Praomys-jacksoni-mkdup-metrics.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/targets-rnor6-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Praomys-jacksoni/Praomys-jacksoni.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Praomys-jacksoni/Praomys-jacksoni-target-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Praomys-jacksoni/Praomys-jacksoni-target-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Praomys-jacksoni/Praomys-jacksoni-avg-target-depth.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/tiles-rnor6-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Praomys-jacksoni/Praomys-jacksoni.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Praomys-jacksoni/Praomys-jacksoni-tile-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Praomys-jacksoni/Praomys-jacksoni-tile-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Praomys-jacksoni/Praomys-jacksoni-avg-tile-depth.txt
samtools flagstat /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Praomys-jacksoni/Praomys-jacksoni.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Praomys-jacksoni/Praomys-jacksoni-flagstats.txt
