#!/bin/bash
samtools merge -r /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Pseudomys-auritus/Pseudomys-auritus.sorted.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Pseudomys-auritus/seq-run-2/53928_HTVLWBCX2_TAATGCG_S16_L001_001.fastp.decon.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Pseudomys-auritus/seq-run-2/53928_HTVLWBCX2_TAATGCG_S16_L002_001.fastp.decon.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Pseudomys-auritus/seq-run-1/53928_HTVLKBCX2_TAATGCG_S16_L002_001.fastp.decon.bam /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Pseudomys-auritus/seq-run-1/53928_HTVLKBCX2_TAATGCG_S16_L001_001.fastp.decon.bam
java -jar ~/bin/picard.jar MarkDuplicates I=/scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Pseudomys-auritus/Pseudomys-auritus.sorted.bam O=/scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Pseudomys-auritus/Pseudomys-auritus.sorted.mkdup.bam M=/scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Pseudomys-auritus/Pseudomys-auritus-mkdup-metrics.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/targets-rnor6-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Pseudomys-auritus/Pseudomys-auritus.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Pseudomys-auritus/Pseudomys-auritus-target-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Pseudomys-auritus/Pseudomys-auritus-target-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Pseudomys-auritus/Pseudomys-auritus-avg-target-depth.txt
samtools depth -b /scratch/gregg_thomas/Murinae-seq/Targets/tiles-rnor6-coords.bed /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Pseudomys-auritus/Pseudomys-auritus.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Pseudomys-auritus/Pseudomys-auritus-tile-depth.tab
awk '{ sum += $3; n++ } END { if (n > 0) print sum / n; }' /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Pseudomys-auritus/Pseudomys-auritus-tile-depth.tab > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Pseudomys-auritus/Pseudomys-auritus-avg-tile-depth.txt
samtools flagstat /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Pseudomys-auritus/Pseudomys-auritus.sorted.mkdup.bam > /scratch/gregg_thomas/Murinae-seq/03B-MappedRat/Pseudomys-auritus/Pseudomys-auritus-flagstats.txt
